parameters.functionScript:
let tire = Inputs.tire;
let epd = Inputs.epd
let pcr = Inputs.pcr
let user = Inputs.users
let arrayIn = Inputs.arrayIn;

let array = [];


let total = 0


if(tire==="Basic"){
    let TObj = Noodl.Object.get()
    TObj.set("Enterprise", false)
    TObj.set("Tire Price", 2750);
    total = total+2750
    if(epd==="Yes"){
        TObj.set("EPD price", 1500)
        total= total+1500 // samma som för EPD
        TObj.set("PCR price", (pcr-2)*750);
        total = total+((pcr-2)*750)
    }
    else{
                TObj.set("PCR price", 0);
                TObj.set("EPD price", 0)
    }

    TObj.set("User Price", (user-1)*990);
    total = total+((user-1)*990)

    TObj.set("Total Price", total);



    let oo = arrayIn.find((item)=>item.Tier===tire);
    if(oo){
        TObj.set("meta", oo);
    }
    array.push(TObj); 
}
if(tire==="PRO"){
    let TObj = Noodl.Object.get()
    TObj.set("Enterprise", false)
    TObj.set("Tire Price", 15000);
    total = total+15000
    if(epd==="Yes"){
        TObj.set("EPD price", 1500)
        total= total+1500 // samma som för EPD
        TObj.set("PCR price", (pcr-2)*750);
        total = total+((pcr-2)*750)
    }
    else{
                TObj.set("PCR price", 0);
                TObj.set("EPD price", 0)
    }

    TObj.set("User Price", (user-15)*990);
    total = total+((user-15)*990)

    TObj.set("Total Price", total);



    let oo = arrayIn.find((item)=>item.Tier===tire);
    if(oo){
        TObj.set("meta", oo);
    }
    array.push(TObj); 
}
if(tire==="Enterprise"){
    let TObj = Noodl.Object.get()
    TObj.set("Enterprise", true)
    TObj.set("Tire Price", 2750);
    total = total+2750
    if(epd==="Yes"){
        TObj.set("EPD price", 1500)
        total= total+1500 // samma som för EPD
        TObj.set("PCR price", (pcr-2)*750);
        total = total+((pcr-2)*750)
    }
    else{
                TObj.set("PCR price", 0);
                TObj.set("EPD price", 0)
    }

    TObj.set("User Price", (user-1)*990);
    total = total+((user-1)*990)

    TObj.set("Total Price", total);



    let oo = arrayIn.find((item)=>item.Tier===tire);
    if(oo){
        TObj.set("meta", oo);
    }
    array.push(TObj); 
}


Outputs.array = array

